mapper.update:
		UPDATE MV_PPL
           SET MDFY_DT=SYSDATE
           	 , MDFYR=#{mdfyr}
           	 , PRFL_PCTR =NVL(#{prflPctr},'baseProfile.png')
           	 , RL_NM=#{rlNm}

           	 , NM=#{nm}


           	 , USE_YN=NVL(#{useYn},'N')  

         WHERE MV_PPL_ID=#{mvPplId}

		UPDATE MV_PPL
           SET DEL_YN='Y' 
         WHERE MV_PPL_ID=#{_parameter}
