mapper.select:
SELECT PWD_SALT
  FROM MBR
 WHERE MBR_ID=#{_parameter}

mapper.update:
		UPDATE MBR
           SET MBR_NM=#{mbrNm}
             , USE_YN=NVL(#{useYn},'N')
             , LGN_TRY_CNT=#{lgnTryCnt}
             , LGN_BLOCK_YN=NVL(#{lgnBlockYn},'N') 
             , ADM_YN=NVL(#{admYn},'N')
		 WHERE MBR_ID=#{mbrId}

		UPDATE MBR
           SET USE_YN= 'N'
		 WHERE MBR_ID=#{_parameter}
